import React from 'react';
import './Board.css';

class Board extends React.Component{
    draw = event => {
        let canvas = document.querySelector('#Board');
        let x = event.pageX;
        let y = event.pageY;
        console.log(x + ' ' + y);
        let ctx = canvas.getContext('2d');
        ctx.beginPath();
        ctx.moveTo(75,50);
        ctx.lineTo(100,75);
        ctx.lineTo(100,25);
        ctx.closePath();
        ctx.fill();
    }
    render() {
        return (
            <div className="Board">
                <canvas id="Board" onMouseDown={(e) => this.draw(e)}></canvas>
            </div>
        );
    }
}

export default Board;
