import React from 'react';
import './Board.css';

class Board extends React.Component{
    draw = event => {
        console.log(event.pageX);
    }
    render() {
        return (
            <div className="Board">
                <canvas id="Board" onMouseDown={(e) => this.draw(e)}></canvas>
            </div>
        );
    }
}

export default Board;
