import React from 'react';
import './Board.css';

class Board extends React.Component{
    draw = event => {
        let canvas = document.querySelector('#Board');
        var rect = canvas.getBoundingClientRect();
        var difx = window.innerWidth - canvas.width;
        var dify = window.innerHeight - canvas.height;
        let x = Math.round( event.screenX * (canvas.width / canvas.offsetWidth) );
        let y = Math.round( event.screenY * (canvas.height / canvas.offsetHeight) );

        console.log(difx + ' ' + dify);
        let ctx = canvas.getContext('2d');
        ctx.fillRect(x,y,1,1);
    }
    render() {
        return (
            <div className="Board">
                <canvas id="Board" onMouseMove={(e) => this.draw(e)}></canvas>
            </div>
        );
    }
}

export default Board;
